import { useSelector, useDispatch } from 'react-redux';
import './App.css';

function App() {
  const counter = useSelector((state) => state.counter);
  const dispatch = useDispatch();

  const increment = () => {
    dispatch({ type: 'INC' });
  };

  return (
    <div>
      <h2>Counter App</h2>
      <h3>{counter}</h3>
      <button inc={increment}>+</button>
      <button dec={decrement}>-</button>
    </div>
  );
}

export default App;
